<script>
export default {
  name: 'Login',
  data() {
    return {
      loading: false,
      redirect: undefined,
      otherQuery: {},
    };
  },
  watch: {
    $route: {
      handler: function(route) {
        const query = route.query;
        if (query) {
          this.redirect = query.redirect;
          this.otherQuery = this.getOtherQuery(query);
        }
      },
      immediate: true,
    },
  },

  methods: {
    // handleLogin() {
    //   this.$refs.loginForm.validate(valid => {
